import React, { useState, useEffect } from 'react';
import http from '../../components/app/http';
import {Row, Col, Form, Button, Table, Modal} from "react-bootstrap"
import Toast from '../../components/app/toast';
// import Button from '../../components/ui/Button';

async function getEncoderList(headers, unauthorized) {
    return fetch('/v1/encoder_list', {
        method: 'GET',
        headers: headers(),
    })
    .then(data => data.json())
    .then(data => {
        if(data?.status === 'ok')
            return {'type': 'info', 'title':'Information', 'body': data.msg}

        unauthorized(data)
        return {'type': 'danger', 'title':'Error', 'body': data.msg.err}
    })
    .catch(e => { return {'type': 'danger', 'title':'Critical Error', 'body': 'Server error'}})
}

async function putEncoderList(items, headers, unauthorized) {
    return fetch('/v1/encoder_list', {
        method: 'PUT',
        headers: headers(),
        body: JSON.stringify(items)
    })
    .then(data => data.json())
    .then(data => {
        if(data?.status === 'ok')
            return {'type': 'info', 'title':'Information', 'body': 'User data successfully updated'}

        unauthorized(data)
        return {'type': 'danger', 'title':'Error', 'body': data.msg.err}
    })
    .catch(e => { return {'type': 'danger', 'title':'Critical Error', 'body': 'Server error'}})
}

export default function EncoderList() {
    const {headers, unauthorized} = http();
    const [items, setItems] = useState([]);
    const [toast, setToast] = useState({});
    const [formRemove, setFormRemove] = useState({'show':false, index: -1});
    const [form, setForm] = useState({'show':false});

    const onSubmit = async e => {
        e.preventDefault();
        const k = {
            'items': items
        }

        await putEncoderList(k, headers, unauthorized)
        .then(res => setToast({...res, show: true}))
    };

    function onAddClicked() {
        toast.show = false
        setForm({
                'show': true,
                'title': "Add a new video stream",
                'button': "Add",
                'name': '',
                'url1': '',
                'url2': '',
                'index': -1
            }
        )
    }
    function onEditClicked(key) {
        toast.show = false
        setForm({
                'show': true,
                'title': "Edit the video stream",
                'button': "Edit",
                'name': items[key].name,
                'url1': items[key].url1,
                'url2': items[key].url2,
                'index': key
            }
        )
    }
    function onRemoveClicked() {
        toast.show = false
        setItems(items.filter((_, index) => index !== formRemove.index))
        setFormRemove({"show": false})
    }

    function addStream(index) {
        toast.show = false
        const pos = index < 0 ? items.length + 1 : items[index].pos
        const item = {
            'name': form.name,
            'url1': form.url1,
            'url2': form.url2,
            'pos':  pos,
            'transport': 'tcp'

        }
        if(index < 0) {
            setItems(
                [
                    ...items
                    , item
                ]
            )
        } else 
            items[index] = item
        setForm({...form, 'show': false})
    }

    function formOnChange(e) {
        toast.show = false
        setForm({
            ...form, 
            [e.target.name]: e.target.value
        });
    }
    function onFormEdit() {
        return form?.url1?.length > 5 && form?.name?.length >= 3

    }

    useEffect(() => {
        getEncoderList(headers, unauthorized)
            .then(res => {
                if(res.type === 'info')
                    setItems(res.body)
                else 
                    setToast({...res, show: true})
            })
    // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [])

    return (
        <>
        <Modal
            show={formRemove?.show}
            onHide={() => setFormRemove(false)}
        >
            <Modal.Body>
                <h5 className="text-center">Do you really want to remove the '{formRemove?.name}' video stream?</h5>
            </Modal.Body>

            <Modal.Footer>
                <Button variant='secondary' onClick={()=> setFormRemove({...formRemove, 'show': false})}>Close</Button>
                <Button variant="primary" onClick={onRemoveClicked}>Remove</Button>
            </Modal.Footer>
        </Modal>
        <Modal
            show={form?.show}
            dialogClassName='modal-100w'
            onHide={() => setForm({...form, 'show': false})}
        >
        <Modal.Header closeButton>
            <Modal.Title>
                {form?.title}
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Group className="mb-3">
                <Form.Label>Name</Form.Label>
                <Form.Control
                    autoFocus
                    type="text"
                    name="name"
                    placeholder="Enter a name"
                    value={form?.name}
                    onChange={formOnChange}
                />
                </Form.Group>
                <Form.Group className="mb-3" controlId="url1">
                    <Form.Label>RTSP stream (high quality)</Form.Label>
                    <Form.Control
                        type="text"
                        name="url1"
                        placeholder="Enter a url"
                        value={form?.url1}
                        onChange={formOnChange}
                    />
                </Form.Group>
                <Form.Group className="mb-3" controlId="url2">
                    <Form.Label>RTSP stream (low quality)</Form.Label>
                    <Form.Control
                        type="text"
                        name="url2"
                        placeholder="Enter a url"
                        value={form?.url2}
                        onChange={formOnChange}
                    />
                </Form.Group>
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button variant='secondary' onClick={()=> setForm({...form, 'show': false})}>Close</Button>
            <Button variant='primary' disabled={!onFormEdit()} onClick={() => addStream(form?.index)}>{form?.button}</Button>
        </Modal.Footer>
        </Modal>
            <Form className="mb-5" onSubmit={onSubmit}>
            <Row>
                <h1>Video streams</h1>
            </Row>
            <Row>
                <Col className="col-10">
                    <h2>Encoder list</h2>
                </Col>
                <Col className="col-2">
                    <Button buttonText={{text: "Добавить"}} onClick={onAddClicked}/>
                    <Button buttonText={{text: "Обновить"}} type="submit" disabled={items?.length < 1}/>
                    <Button buttonText={{text: "Удалить"}}/>
                    {/* <Button className='mb-3 mx-2' onClick={onAddClicked}>Add</Button>
                    <Button className='mb-3' type="submit" disabled={items?.length < 1}>Update</Button> */}
                </Col>
            </Row>
            <hr />
            <Row>
                <Table striped bordered hover variant='dark'>
                    <thead>
                        <tr>
                            <th>#</th>
                            <th>Name</th>
                            <th>Address 1</th>
                            <th>Address 2</th>
                            <th>Controls</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                            items.map((item, index) => 
                                <tr key={index}>
                                    <th>
                                        <Form.Check type="checkbox" />
                                    </th>
                                    <th>{item.name}</th>
                                    <th>{item.url1}</th>
                                    <th>{item.url2}</th>
                                    <th>
                                        <Button className="mx-2" onClick={() => onEditClicked(index)}>Edit</Button>
                                        <Button onClick={() => setFormRemove({"show":true, 'index': index, "name": item.name})}>Remove</Button>
                                    </th>
                                </tr>
                            )

                        }
                    </tbody>
                </Table>
            </Row>
            <Toast show={toast?.show} type={toast?.type} title={toast?.title} body={toast?.body}/>
            </Form>
        </>
    );
}
