import React from 'react';
import {Navbar, Nav, Container, Row, Col, Form, Button} from "react-bootstrap"

import {
    BrowserRouter,
    Routes,
    Route,
} from 'react-router-dom';

import './App.css';
import Login from './components/login';
import Logout from './components/logout';
import EncodeList from './components/encoder_list';
import Options from './components/options';
import Users from './components/users';
import Key from './components/key';
import VideoWall from './components/video_wall';
import http from './components/app/http';
import "bootstrap/dist/css/bootstrap.min.css";
import MyLink from './components/ui/MyLink';
// import logo from './logo.svg'

//https://www.digitalocean.com/community/tutorials/how-to-add-login-authentication-to-react-applications/
//https://reacttraining.com/blog/react-router-v6-pre//

function App() {
    const { token, setToken } = http();

    if(!token) {
        return <Login setToken={setToken} />
    }

    return (
        <>
            <Navbar collapseOnSelect expand="md" bg="dark" variant="dark">
                <Container fluid>
                    <Navbar.Brand href="/">
                        {/* <img alt="logo" src={logo} height='30' width='30' className='d-inline-block align-top'/> */}
                        VideoWall
                    </Navbar.Brand>
                    <Navbar.Toggle aria-controls='responsive-navbar-nav' />
                    <Navbar.Collapse id='responsive-navbar-nav'>
                        <Nav className='me-auto'>
                            <Nav.Link href='/'> Encoder List </Nav.Link>
                            <Nav.Link href='/videowall'> VideoWall </Nav.Link>
                            <Nav.Link href='/users'> Users </Nav.Link>
                            <Nav.Link href='/logout'>Logout</Nav.Link>
                            <Nav.Link href='/options'> Options </Nav.Link>
                            <Nav.Link href='/key'> Key </Nav.Link>
                        </Nav>
                        <Form inline>
                            <Button variant='outline-info'> Search </Button>
                        </Form>
                    </Navbar.Collapse>
                </Container>
            </Navbar>

            <BrowserRouter>
                <Container fluid>
                    <Row>
                        <Col>
                            <Routes>
                                <Route exact path="/" element={<EncodeList/>}/>
                                <Route exact path="/videowall" element={<VideoWall/>}/>
                                <Route exact path="/users" element={<Users/>}/>
                                <Route exact path="/logout" element={<Logout/>}/>
                                <Route exact path="/options" element={<Options/>}/>
                                <Route exact path="/key" element={<Key/>}/>
                            </Routes>
                        </Col>
                    </Row>
                </Container>
            </BrowserRouter>
        </>
    );
}

export default App;
