    const[selected, setSelected] = useState("Раскладка монитора");
    const[selid, setSelid] = useState(0);
    // const options = ["1x1 сплит", "2x2 сплит", "3x2 сплит", "3x3 сплит", "3x4 сплит", "4x4 сплит", "4x6 сплит", "5+1 сплит", "12+1 сплит"];
    const [isActive] = useState(false);
    return (
        <>
        <Modal
            show={formRemove?.show}
            onHide={() => setFormRemove(false)}
        >
