                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                </div> 
                                )
                            }

                            {  selid == 6 &&(
                                <div className="screen__seven-split split">
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                </div> 
                                )
                            }

                            {  selid == 7 &&(
                                <div className="screen__eight-split split">
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                    <div></div>
                                </div> 
                                )
                            }

                            {  selid == 8 &&(
                                <div className="screen__second-split">
                                    <div></div>
                                    <div></div>

                                </div> 
                                )
                            }
                        </div>
                    </form>
                </div>
            </div>
            {/* <Form className="mb-5" onSubmit={onSubmit}>
                <Row>
                    <Col className="col-10">
                        <h1>VideoWall</h1>
                    </Col>
                    <Col className="col-2">
                        <MyButton buttonText={{text: "Обновить"}} type="submit" disabled={vmItems?.items.length < 1}/>
                    </Col>
                </Row>
                <hr />
                <DragDropContext onDragEnd={onDragEnd} >
                    <Droppable droppableId="1">
                      {(provided) => (
                    <Row>
                    <Col className="col-2">

                            </Col>
                            <Col className="col-8"  ref={provided.innerRef} {...provided.draggableProps} {...provided.dragHandleProps}>
                                {
                                    schema === "3x3" &&
                                        <>
                                        {
                                            vmItems.items.map((item, index) =>  
