import React, { useState } from 'react';
import Http from '../../components/app/http';
import {Row, Col, Form} from "react-bootstrap"
import Toast from '../../components/app/toast';
import MyButton from '../ui/MyButton';

async function doRestart(headers, unauthorized) {
    return fetch('/v1/server/restart/system', {
        method: 'POST',
        headers: headers()
    })
    .then(data => data.json())
    .then(data => {
        if(data?.status === 'ok')
            return {'type': 'info', 'title':'Information', 'body': 'Restart successfully'}

        unauthorized(data)
        return {'type': 'danger', 'title':'Error', 'body': data.msg.err}
    })
    .catch(e => { return {'type': 'danger', 'title':'Critical Error', 'body': 'Server error'}})
}
async function doStop(headers, unauthorized) {
    return fetch('/v1/server/stop/system', {
        method: 'POST',
        headers: headers()
    })
    .then(data => data.json())
    .then(data => {
        if(data?.status === 'ok')
            return {'type': 'info', 'title':'Information', 'body': 'Stop successfully'}

        unauthorized(data)
        return {'type': 'danger', 'title':'Error', 'body': data.msg.err}
    })
    .catch(e => { return {'type': 'danger', 'title':'Critical Error', 'body': 'Server error'}})
}

export default function Options() {
    const {headers, unauthorized} = Http();
    const [toast, setToast] = useState({});

    const restart = async e => {
        doRestart(headers, unauthorized)
            .then(res => setToast({...res, show: true}));
    }
    const stop = async e => {
        doStop(headers, unauthorized)
            .then(res => setToast({...res, show: true}));
    }

    return (
        <>  
            <h2 className="content__title">Обновления</h2>
            <div className="content__window">
                <div className="content__window-top">
                    <h3 className="content__window-title">УПРАВЛЕНИЕ НАСТРОЙКАМИ СИСТЕМЫ</h3>
                    <MyButton buttonText={{text: "Обновить"}} type="submit" disabled={key?.key < 10}/> 
                </div>
                <hr />

                <form className="content__form" onSubmit={onSubmit}> 
                    <div className="content__form-block">
                        <div className="form__wrapper">
                            {
                                !key && <h6>Loading....</h6>
                            }
                            <div className="form__block-input" controlId="key">
                                <label className="form__label">Enter a license key</label>
                                <input
                                    className="form__input"
                                    type="text"
                                    name="key"
                                    placeholder="Enter a key"
                                    value={key?.key}
                                    onChange={onChange}
                                ></input>
                            </div>
                            <div className="form__block-input disabled">
                                <label className="form__label">Mac address</label>
                                <input
                                    className="form__input"
                                    type="text"
                                    value={key?.mac}
                                    disabled
                                ></input>
                            </div>
                        </div>
                        {
                        key?.status &&
                            <div>
                                <Badge pill bg={key?.status === 'valid' ? 'success' : 'danger'} className="mt-5">
                                    {key?.status === 'valid' ? 'Valid' : 'Invalid'}
                                </Badge>
                            </div>
                        }
                    </div>
                </form>
                <Toast show={toast?.show} type={toast?.type} title={toast?.title} body={toast?.body}/>
            </div>
            <Form className="mb-5">
                <Row>
                    <h1>Options</h1>
                </Row>
                <Row>
                    <h2>Options administration</h2>
                </Row>
                <hr />
                <Row>
                    <Col>
                        <h6>Restart</h6>
                        <MyButton buttonText={{text: "Обновить"}} onClick={restart}/>
                    </Col>
                </Row>
                <Row>
                    <Col>
                        <h6>Stop</h6>
                        {/* <Button className='mb-3' onClick={stop}>Update</Button> */}
                        <MyButton buttonText={{text: "Остановить"}} onClick={stop}/>
                    </Col>
                </Row>
                <Toast show={toast?.show} type={toast?.type} title={toast?.title} body={toast?.body}/>
            </Form>
        </>
    )
}
