import React, { useState, useEffect } from 'react';


function MyDropdown (selected, setSelected) {
    const [isActive, setIsActive] = useState(false);
    const options = ['1x1 сплит', '2x2 сплит', '3x2 сплит'];
    return (
        <div className="undertop__dropdown">
            <div className="dropdown__default" onClick={(e) => setIsActive(!isActive)}>
                <span>{selected} </span>
                <svg width="8" height="6" viewBox="0 0 8 6" fill="none" xmlns="http://www.w3.org/2000/svg">
                    <path d="M0.94 5.27337L4 2.22004L7.06 5.27337L8 4.33337L4 0.333374L0 4.33337L0.94 5.27337Z" fill="#F4F4F4"/>
                </svg>
            </div>
            { isActive && (
                <ul className="dropdown__list">
                    {options.map((option) => {
                        <li onClick = { (e) => { setSelected(option), setIsActive(false)}}>
                            {option}
                        </li>
                    })}
                </ul>
            )}
        </div>
    )
}

export default MyDropdown;
